import React, { useState, useEffect } from 'react';
import './AdminExits.css';

function AdminExits() {
  const [vehicles, setVehicles] = useState([]);
  const [selectedVehicle, setSelectedVehicle] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [odemeYontemi, setOdemeYontemi] = useState('kart');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Araçları çek
  const fetchVehicles = async () => {
    try {
      const response = await fetch('/api/giris');
      const data = await response.json();
      setVehicles(data);
    } catch (error) {
      console.error('Araçlar çekilirken hata:', error);
      setError('Araçlar çekilirken hata oluştu.');
    }
  };

  useEffect(() => {
    fetchVehicles();
  }, []);

  // Çıkış işlemi
  const handleExit = async (arac_plaka) => {
    const confirmExit = window.confirm(`Araç plakası ${arac_plaka} için çıkış yapmak istediğinize emin misiniz?`);
    if (!confirmExit) return;

    try {
      const response = await fetch('/api/cikis', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ arac_plaka, odeme_yontemi }),
      });

      const result = await response.json();
      if (!response.ok) {
        throw new Error(result.error || 'Çıkış işlemi başarısız');
      }

      setSuccess(`Çıkış başarıyla yapıldı. Ödeme: ${result.ucret} TL, Yöntem: ${result.odeme_yontemi}`);
      fetchVehicles();
    } catch (error) {
      console.error('Çıkış işlemi sırasında hata:', error);
      setError(error.message);
    }
  };

  const filteredVehicles = vehicles.filter((vehicle) =>
    vehicle.arac_plaka.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="admin-exits-container">
      <h2>Çıkış İşlemleri</h2>
      {error && <div className="error">{error}</div>}
      {success && <div className="success">{success}</div>}
      
      <input
        type="text"
        placeholder="Plaka ile Ara"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      <label>Ödeme Yöntemi:</label>
      <select value={odemeYontemi} onChange={(e) => setOdemeYontemi(e.target.value)}>
        <option value="kart">Kart</option>
        <option value="nakit">Nakit</option>
      </select>

      <table>
        <thead>
          <tr>
            <th>Plaka</th>
            <th>Giriş Saati</th>
            <th>Çıkış Durumu</th>
            <th>Çıkış</th>
          </tr>
        </thead>
        <tbody>
          {filteredVehicles.map((vehicle) => (
            <tr key={vehicle.giris_ID}>
              <td>{vehicle.arac_plaka}</td>
              <td>{new Date(vehicle.giris_saat).toLocaleString()}</td>
              <td>{vehicle.cikis_durumu}</td>
              <td>
                {vehicle.cikis_durumu === 'çıkmadı' ? (
                  <button onClick={() => handleExit(vehicle.arac_plaka)}>Çıkış Yap</button>
                ) : (
                  'Çıkış Yapıldı'
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AdminExits;
