// src/AdminDashboard.js
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import AdminEmployees from './AdminEmployees'; // Çalışanlar sayfası bileşenini içe aktarıyoruz
import AdminParking from './AdminParking';
import './AdminDashboard.css';

function AdminDashboard({ adminName = "Admin" }) {
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [activePage, setActivePage] = useState('raporlar');
  const [calisanlar, setCalisanlar] = useState([]);
  const [otoparklar, setOtoparklar] = useState([]);
  const [parkedVehicles, setParkedVehicles] = useState([]);
  const [dolulukData, setDolulukData] = useState(null);
  const [kazancData, setKazancData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  useEffect(() => {
    // Tüm verileri paralel olarak çekelim:
    const fetchData = async () => {
      try {
        const [
          calisanRes,
          otoparkRes,
          parkedRes,
          dolulukRes,
          kazancRes
        ] = await Promise.all([
          fetch('/api/calisanlar'),
          fetch('/api/otoparklar'),
          fetch('/api/parkedVehicles'),
          fetch('/api/report/doluluk'),
          fetch('/api/report/kazanc')
        ]);
        const calisanData = await calisanRes.json();
        const otoparkData = await otoparkRes.json();
        const parkedData = await parkedRes.json();
        const dolulukData = await dolulukRes.json();
        const kazancData = await kazancRes.json();

        setCalisanlar(calisanData);
        setOtoparklar(otoparkData);
        setParkedVehicles(parkedData);
        setDolulukData(dolulukData);
        setKazancData(kazancData);
        setLoading(false);
      } catch (err) {
        console.error('Veriler çekilirken hata:', err);
        setError('Veriler çekilirken bir hata oluştu.');
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) return <div>Yükleniyor...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="admin-dashboard-container">
      <header className="admin-header">
        <h1>Admin Paneli</h1>
        <div className="admin-info">
          <span>{adminName}</span>
        </div>
      </header>

      <div className="admin-content">
        <aside className={`admin-sidebar ${sidebarOpen ? 'open' : 'closed'}`}>
          <button className="toggle-btn" onClick={toggleSidebar}>
            {sidebarOpen ? '<' : '>'}
          </button>
          {sidebarOpen && (
            <nav className="sidebar-nav">
              <ul>
                <li className={activePage === 'raporlar' ? 'active' : ''}>
                  <button onClick={() => setActivePage('raporlar')}>Raporlar</button>
                </li>
                <li className={activePage === 'calisanlar' ? 'active' : ''}>
                  <button onClick={() => setActivePage('calisanlar')}>Çalışanlar</button>
                </li>
                <li className={activePage === 'otopark' ? 'active' : ''}>
                  <button onClick={() => setActivePage('otopark')}>Otopark</button>
                </li>
                <li className={activePage === 'araclar' ? 'active' : ''}>
                  <button onClick={() => setActivePage('araclar')}>Araçlar</button>
                </li>
                <li className={activePage === 'girisler' ? 'active' : ''}>
                  <button onClick={() => setActivePage('girisler')}>Girişler</button>
                </li>
                <li className={activePage === 'cikislar' ? 'active' : ''}>
                  <button onClick={() => setActivePage('cikislar')}>Çıkışlar</button>
                </li>
                <li className={activePage === 'odemeler' ? 'active' : ''}>
                  <button onClick={() => setActivePage('odemeler')}>Ödemeler</button>
                </li>
              </ul>
            </nav>
          )}
        </aside>

        <main className="admin-main">
          {activePage === 'raporlar' ? (
            <div className="raporlar-content">
              <section className="chart-section">
                <div className="chart-box">
                  <h3>Otopark Doluluk Oranı</h3>
                  <div className="chart-placeholder">
                    {dolulukData ? `${dolulukData.doluluk}%` : 'Veri Yok'}
                  </div>
                </div>
                <div className="chart-box">
                  <h3>Kazanç Oranı</h3>
                  <div className="chart-placeholder">
                    {kazancData ? `${kazancData.kazanc} TL` : 'Veri Yok'}
                  </div>
                </div>
              </section>
              <section className="parked-vehicles">
                <h3>Otoparkta Bulunan Araçlar</h3>
                <table>
                  <thead>
                    <tr>
                      <th>Plaka</th>
                      <th>Giriş Saati</th>
                    </tr>
                  </thead>
                  <tbody>
                    {parkedVehicles.length > 0 ? (
                      parkedVehicles.map((vehicle, index) => (
                        <tr key={index}>
                          <td>{vehicle.plaka}</td>
                          <td>{vehicle.giris_saat}</td>
                        </tr>
                      ))
                    ) : (
                      <tr>
                        <td colSpan="2">Otoparkta araç bulunamadı.</td>
                      </tr>
                    )}
                  </tbody>
                </table>
              </section>
            </div>
          ) : activePage === 'calisanlar' ? (
            <AdminEmployees />
          ) : (
            <div className="placeholder-page">
              <h2>{activePage.charAt(0).toUpperCase() + activePage.slice(1)} Sayfası</h2>
              <p>Bu sayfa henüz tasarlanmadı.</p>
            </div>
          )}
        </main>
      </div>
    </div>
  );
}

export default AdminDashboard;
