// src/AdminExits.js
import React, { useState, useEffect } from 'react';
import './AdminExits.css';

function AdminExits() {
  const [vehicles, setVehicles] = useState([]);
  const [exits, setExits] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [error, setError] = useState('');
  const [selectedPlaka, setSelectedPlaka] = useState('');
  const [showPaymentPopup, setShowPaymentPopup] = useState(false);
  const [selectedPaymentMethod, setSelectedPaymentMethod] = useState('');

  useEffect(() => {
    fetchVehicles();
    fetchExits();
  }, []);

  const fetchVehicles = async () => {
    try {
      const res = await fetch('/api/giris');
      const data = await res.json();
      setVehicles(data.filter(v => v.cikis_durumu === 'çıkmadı'));
    } catch {
      setError('Araçlar alınamadı.');
    }
  };

  const fetchExits = async () => {
    try {
      const res = await fetch('/api/cikis');
      const data = await res.json();
      setExits(data);
    } catch {
      setError('Çıkışlar alınamadı.');
    }
  };

  const openPaymentPopup = (plaka) => {
    setSelectedPlaka(plaka);
    setShowPaymentPopup(true);
  };

  const handleExit = async () => {
    if (!selectedPaymentMethod) {
      setError('Lütfen bir ödeme yöntemi seçin.');
      return;
    }

    try {
      const res = await fetch('/api/cikis', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ arac_plaka: selectedPlaka, odeme_yontemi: selectedPaymentMethod }),
      });
      if (!res.ok) {
        const err = await res.json();
        throw new Error(err.error);
      }

      await fetchVehicles();
      await fetchExits();
      setShowPaymentPopup(false);
      setSelectedPaymentMethod('');
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="admin-exits-container">
      <div className="left-panel">
        <h2>Otoparktaki Araçlar</h2>
        <input
          placeholder="Plaka ara..."
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
        />
        {error && <div className="error">{error}</div>}
        <table>
          <thead>
            <tr>
              <th>Plaka</th><th>Giriş Saati</th><th>Yer</th><th>Çıkış</th>
            </tr>
          </thead>
          <tbody>
            {vehicles.filter(v => v.arac_plaka.includes(searchTerm)).map(v => (
              <tr key={v.giris_ID}>
                <td>{v.arac_plaka}</td>
                <td>{new Date(v.giris_saat).toLocaleString()}</td>
                <td>{v.yer_ad}</td>
                <td><button onClick={() => openPaymentPopup(v.arac_plaka)}>Çıkış Yap</button></td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="right-panel">
        <h2>Çıkış Yapan Araçlar</h2>
        <table>
          <thead>
            <tr>
              <th>Çıkış ID</th><th>Plaka</th><th>Giriş Saati</th><th>Çıkış Saati</th><th>Ödeme</th><th>Ücret</th>
            </tr>
          </thead>
          <tbody>
            {exits.map(e => (
              <tr key={e.cikis_ID}>
                <td>{e.cikis_ID}</td>
                <td>{e.arac_plaka}</td>
                <td>{new Date(e.giris_saat).toLocaleString()}</td>
                <td>{new Date(e.cikis_saati).toLocaleString()}</td>
                <td>{e.odeme_yontemi}</td>
                <td>{e.ucret} TL</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {showPaymentPopup && (
        <div className="popup">
          <div className="popup-inner">
            <h3>Ödeme Yöntemi Seçiniz</h3>
            <select value={selectedPaymentMethod} onChange={(e) => setSelectedPaymentMethod(e.target.value)}>
              <option value="">Seçiniz</option>
              <option value="kart">Kart</option>
              <option value="nakit">Nakit</option>
            </select>
            <button onClick={handleExit}>Onayla</button>
            <button onClick={() => setShowPaymentPopup(false)}>İptal</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default AdminExits;
