// App.js
import React, { useState, useEffect } from 'react';
import { createBrowserRouter, RouterProvider, Navigate } from 'react-router-dom';
import Login from './Login';
import AdminDashboard from './AdminDashboard';
import KullaniciDashboard from './KullaniciDashboard';

function App() {
  const [user, setUser] = useState(null);

  // Sayfa yenilendiğinde localStorage'dan kullanıcı bilgilerini çek
  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem('user'));
    if (storedUser) {
      setUser(storedUser);
    }
  }, []);

  // Kullanıcı giriş yaptığında çalışacak fonksiyon
  const handleLogin = (userData) => {
    setUser(userData);
    localStorage.setItem('user', JSON.stringify(userData));
  };

  // Çıkış fonksiyonu
  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('user');
  };

  // Route yapılandırması
  const router = createBrowserRouter([
    {
      path: '/',
      element: <Login onLogin={handleLogin} />,
    },
    {
      path: '/admin',
      element: user?.rol === 'admin' ? (
        <AdminDashboard user={user} onLogout={handleLogout} />
      ) : (
        <Navigate to="/" replace />
      ),
    },
    {
      path: '/kullanici',
      element: user?.rol === 'personel' ? (
        <KullaniciDashboard user={user} onLogout={handleLogout} />
      ) : (
        <Navigate to="/" replace />
      ),
    },
    {
      path: '*',
      element: <Navigate to="/" replace />,
    },
  ]);

  return <RouterProvider router={router} />;
}

export default App;
